targets.expr:
(
	min by (group,network)
	(
		(
			max by (group,network,pubkey)
			(
				validator_next_proposal_slot{group="$group"} > scalar(floor((time() - last_over_time(consensus_beacon_genesis_time{group="$group"}[2h]))/12))
			) * 12
		)
	)
	+
	scalar
	(
		last_over_time
		(
			consensus_beacon_genesis_time{group="$group"}[2h]
		)
	)
) * 1000
